const testWrapper=document.querySelector(".test-wrapper");const testArea=document.querySelector("#test-area");const originText=document.querySelector("#origin-text p").innerHTML;const resetButton=document.querySelector("#reset");const theTimer=document.querySelector(".timer");var timer=[0,0,0,0];var interval;var timerRunning=false;function leadingZero(tim)
{if(tim<=9)
{var tim="0"+tim;}
return tim;}
function runTimer()
{var currentTime=leadingZero(timer[0])+":"+leadingZero(timer[1])+":"+leadingZero(timer[2]);timer[3]++;theTimer.innerHTML=currentTime;timer[0]=Math.floor((timer[3]/100)/60);timer[1]=Math.floor((timer[3]/100)-(timer[0]*60));timer[2]=Math.floor(timer[3]-(timer[1]*100)-(timer[0]*6000));}
function spellCheck()
{let textEntered=testArea.value;let originTextMatch=originText.substring(0,textEntered.length);if(textEntered==originText)
{clearInterval(interval);testWrapper.style.borderColor="green";console.log("orange");}
else if(textEntered==originTextMatch)
{testWrapper.style.borderColor="pink";console.log("green");}
else
{testWrapper.style.borderColor="red";}
console.log(textEntered);}
function Start()
{let textEnteredLength=testArea.value.length;console.log(textEnteredLength);if(textEnteredLength===0&!timerRunning)
{timerRunning=true;interval=setInterval(runTimer,10);}}
function reset()
{clearInterval(interval);interval="null";timerRunning=false;timer=[0,0,0,0];testArea.value="";theTimer.innerHTML="00:00:00";testWrapper.style.borderColor="grey";}
testArea.addEventListener("keypress",Start,false);testArea.addEventListener("keyup",spellCheck,false);resetButton.addEventListener("click",reset,false);